import giimport timegi.require_version('Gtk', '3.0')from gi.repository import Gtk,Gdkimport datetimefrom MySQLdb import OperationalErrorfrom copy import deepcopy#	Generated By Glade To Python: by Rocky Nuarin, Aug 2017 Phils#	#####################www.WireThemAll.com#####################\def reconnectMySql(db):        print ("DB has Run Away, pinging to reconnect")        db.ping(True)def GetSingleRec(db,Table, Fields, Fieldsval,retvalfields):        curs=db.cursor()        #connectDBifRunAway()        if type(Fieldsval)==int or type(Fieldsval)==float or type(Fieldsval)==long:                sql="select * from " + Table + " where " + Fields + " = " + Fieldsval         else:#might be string or date or time                    sql="select * from " + Table + " where " + Fields + " = '" + str(Fieldsval) + "'"        #curs.execute (sql)        for a in range(2):                try:                        curs.execute (sql)                        break                except OperationalError as e:                        #do what you want to do on the error                        reconnectMySql(db)        results = curs.fetchall()        col=getFields(db,Table)        if len(results)==0:                return ""        for a in range(len(col)):                if col[a]==retvalfields:                        return results[0][a]#first record only        return ""def getFields(db,cols):        #connectDBifRunAway()        curs=db.cursor()        sql="SHOW COLUMNS FROM " + cols        for a in range(2):                try:                        curs.execute (sql)                        break                except OperationalError as e:                        #do what you want to do on the error                        reconnectMySql(db)        results = curs.fetchall()        ret=[]        for a in results:                ret.append(a[0])        return ret#ExecuteNonQuerry(db,"INSERT INTO data (Name, Date, Time) VALUES ('%s', '%s', '%s')" % ("Bago", Date(),Time()) )#ExecuteNonQuerry(db,"UPDATE data SET Name='%s' WHERE Name='%s'" % ("Wala","raks"))		#ExecuteNonQuerry(db,"DELETE FROM data WHERE Name='%s'" % ("Bago"))	def ExecuteNonQuerry(db,sql):        curs=db.cursor()        for a in range(2):                try:                        curs.execute (sql)                        break                except OperationalError as e:                        #do what you want to do on the error                        connectMySql()        db.commit()        return ""def getRecords(db,sql):        #connectDBifRunAway()        #curs.execute (sql)        curs=db.cursor()        for a in range(2):                try:                        curs.execute (sql)                        break                except OperationalError as e:                        #do what you want to do on the error                        reconnectMySql(db)        results = curs.fetchall()        return resultsdef getType(x):        if type(x) is str:                return str        if type(x) is long:                return int        if x == None:                return str            if type(x) is datetime.date:                return str #datetime.date        if type(x) is datetime.datetime:                return str #datetime.date            if type(x) is datetime.timedelta:                return str #datetime.timedelta            if type(x) is int:                return int        if type(x) is datetime.time:                return str #datetime.date                if type(x) is datetime.timedelta:                return str #datetime.date    def getTable(sql,after="from"):        flag=False        x=sql.split(" ")        for a in x:                if flag==True:                        if a.lower() in ["\r","\t","\n","\t"]:                                continue                        else:                                return a                if a.lower()==after:                        flag=True        class TreeViewFilterWindow:                #Creating the ListStore model#        _fields=["Name","Age","Address"]#        _listdata=[["1Rocky Nuarin","23","Malate Manila"],["2Rocky Nuarin","23","Malate Manila"],["3Rocky Nuarin","23","Malate Manila"],["4Rocky Nuarin","23","Malate Manila"]]        def __init__(self,scrolledwindow1,checkbox=False,clickISR=None):                self.obj=[]                self.scrolledwindow1=scrolledwindow1                self.checkbox=checkbox                self.table = Gtk.Layout()                self.table.set_size_request(2000,800)                                self.table.set_visible(True)			#importtant                self.scrolledwindow1.add_with_viewport(self.table)	                self.clickISR=clickISR          def PlaceGridRow(self,rowdata,i=0,header=False,width=100,height=30):                rowsobject=[]                for j,data in enumerate(rowdata):                        caption = "Btn"+str(j)+str(i)                        #btn = Gtk.Button(data)		                        ##btn = Gtk.Label()	                        ##btn.set_text(caption)                        ##btn.connect("button-press-event",self.click)                        #btn.connect("button-press-event",self.click)                        #btn.set_size_request(width,height)                        ##btn.set_markup("<b><i>"+ data +"</i></b>")                        if header:                                #btn = Gtk.Button(data)		                                btn=Gtk.Entry()                                btn.set_text(data)                                                                btn.override_color(Gtk.StateType.NORMAL, Gdk.RGBA(0,0,1,1))                                 btn.modify_bg(Gtk.StateType.NORMAL, Gdk.Color(0,0,0))                                a=0                        else:                                btn=Gtk.Entry()                                btn.set_text(str(data))                                a=0                                #btn.override_color(Gtk.StateType.NORMAL, Gdk.RGBA(0,0,0,0))                                 #btn.modify_bg(Gtk.StateType.NORMAL, Gdk.Color(0,0,0))					                        btn.connect("button-press-event",self.click)                        btn.set_size_request(width,height)                                                        self.table.put(btn, (j)*width,(i)*height)                        btn.set_visible(True)	#importtant	                        self.obj.append(btn)                        self.row[btn]=i-1                        self.col[btn]=j                        rowsobject.append(btn)                self.table.set_size_request(2000,800)                         self.rowsobject.append(rowsobject)                        def HighlightSelected(self,row):                if row in self.rowselected:                        for btn in self.rowsobject[row]:                                btn.override_color(Gtk.StateType.NORMAL, Gdk.RGBA(0,0,0,1))                                                                 self.rowselected.remove(row)                        else:                        for btn in self.rowsobject[row]:                                btn.override_color(Gtk.StateType.NORMAL, Gdk.RGBA(1,0,0,1))                                         self.rowselected.append(row)          def DeleteSelectedValues(self):                if len(self.rowselected)!=0:                        buf=[]                        for i in range(0,len(self.values)):                                if (i+1) in self.rowselected:                                        i=i                                else:                                        buf.append(self.values[i])                        self.values=buf                        self.rowselected=[]                        self.Refresh()                                         def getSelectedValues(self):                ret=[]                for a in self.rowselected:                        ret.append(self.values[a-1])                return ret        def getSelectedIndex(self):                ret=[]                for a in self.rowselected:                        ret.append(a-1)                return ret        def AddItem(self,value):                self.values.append(value)                self.Refresh()                               def AddItems(self,values):                for a in values:                        self.values.append(a)                self.Refresh()                                def click(self,*arg):                r,c= self.row[arg[0]],self.col[arg[0]]                if r==-1:return                #print r,c,self.values[r]                if self.clickISR!=None:                        self.clickISR(self.values[r])#(r,c,self.values[r])                ret=self.HighlightSelected(r+1)                print (self.getSelectedValues())                print (self.getSelectedIndex())                pass        def Refresh(self):                self.Display(self.fields, self.values, None)        def Display(self,fields,listdata,_checkbox):                for a in self.obj:                        self.table.remove(a)                                 self.obj=[]                self.row={}                self.col={}                self.rowsobject=[]                self.rowselected=[]                self.values=deepcopy(listdata)                self.fields=deepcopy(fields)                  self.PlaceGridRow(self.fields,0,True)		                self.table.set_size_request(100*len(self.fields),(len(self.values)+1)*31)                 for i,rowdata in enumerate(self.values):                        self.PlaceGridRow(rowdata,i+1)                                           class Handler:        def __init__(self,clickISR=None,checkbox=False):                self.clickISR=clickISR                builder = Gtk.Builder()                builder.add